import React from "react";

const FileTree = ({ nodes, showDuplicates }) => {
  if (!nodes || nodes.length === 0) return null;

  const renderTree = (node) => {
    const isFolder = node.mime_type === "application/vnd.google-apps.folder";
    const displayName = isFolder ? (
      <strong>{node.name}</strong>
    ) : (
      <a
        href={`https://drive.google.com/file/d/${node.file_id}/view`}
        target="_blank"
        rel="noopener noreferrer"
        style={{ textDecoration: "none", color: "blue" }}
      >
        {node.name}
      </a>
    );

    return (
      <li key={node.file_id}>
        {displayName}
        {showDuplicates && node.duplicate_count && (
          <span style={{ marginLeft: "0.5rem", color: "red" }}>
            (x{node.duplicate_count} copies)
          </span>
        )}
        {node.children && node.children.length > 0 && (
          <ul>{node.children.map(renderTree)}</ul>
        )}
      </li>
    );
  };

  return <ul>{nodes.map(renderTree)}</ul>;
};

export default FileTree;
